#### determine longest records #

min(dat$dateAK[!is.na(dat$CPCRW.Caribou.Peak..AirTemp_1000cm.CPEAK..C.)], na.rm=T) #1998
min(dat$dateAK[!is.na(dat$CPCRW.Caribou.Peak..AirTemp_200cm.CPEAK..C.)], na.rm=T) #1998
